-- ----------------------------
-- Table structure for bms_book
-- ----------------------------
DROP TABLE IF EXISTS `bms_book`;
CREATE TABLE `bms_book`  (
  `id` bigint unsigned NOT NULL COMMENT '自增id',
  `name` varchar(64) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '图书名称',
  `create_time` datetime(0) NOT NULL COMMENT '记录创建时间',
  `modify_time` datetime(0) NOT NULL COMMENT '记录修改时间',
  `category_id` bigint unsigned NOT NULL COMMENT '图书分类号',
  `description` varchar(1000) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '图书描述',
  `picture` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '图书图片url',
  `price` decimal(10, 2) UNSIGNED NOT NULL COMMENT '价格',
  `sale` int unsigned NOT NULL COMMENT '图书销量',
  `stock` int unsigned NOT NULL COMMENT '图书库存',
  `isbn` char(13) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '书号',
  `author` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '作者名，如果有多个作者，以逗号分隔',
  `press` varchar(30) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '出版社名',
  `language` varchar(10) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '图书语言',
  `page_count` int(0) NULL DEFAULT NULL COMMENT '图书页数',
  `publish_date` datetime(0) NULL DEFAULT NULL COMMENT '出版日期',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `idx_name`(`name`) USING BTREE COMMENT '图书名索引',
  INDEX `idx_isbn`(`isbn`) USING BTREE COMMENT '图书号索引',
  INDEX `idx_category_id`(`category_id`) USING BTREE COMMENT '分类号索引',
  INDEX `idx_press`(`press`) USING BTREE COMMENT '出版社索引',
  INDEX `price`(`price`) USING BTREE,
  CONSTRAINT `FK_category_id` FOREIGN KEY (`category_id`) REFERENCES `bms_category` (`id`) ON DELETE RESTRICT ON UPDATE RESTRICT
) ENGINE = InnoDB AUTO_INCREMENT = 281 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci ROW_FORMAT = Dynamic;

