INSERT INTO `bms_book` VALUES (1, '中华人民共和国民法典', '2020-12-24 00:00:00', '2020-12-26 00:00:00', 9, '《中华人民共和国民法典》被称为“社会生活百科全书”，是民事权利的宣言书和保障书。本书收录的是《中华人民共和国民法典》标准文本，共7编，依次为总则编、物权编、合同编、人格权编、婚姻家庭编、继承编、侵权责任编以及附则，并按照权威版本进行了全文折校，保证文字的精准性', 'http://image31.bookschina.com/2020/zuo/6/s8318234.jpg', 56.00, 0, 99, '9787010221571', '中国法制出版社', '中国法制出版社', '', 266, '2020-06-01 00:00:00');
INSERT INTO `bms_book` VALUES (2, '刑法学讲义', '2020-12-24 00:00:00', '2020-12-26 00:00:00', 9, '“法律男神”罗翔教授为每一位普通读者写的刑法普及讲义。', 'http://image12.bookschina.com/2020/20200828/1/s8366493.jpg', 64.50, 0, 99, '9787222193727', '罗翔，果麦文化 出品', '云南人民出版社', '', 359, '2020-07-01 00:00:00');
INSERT INTO `bms_book` VALUES (3, '法的门前', '2020-12-24 00:00:00', '2020-12-26 00:00:00', 9, '既汲取了原书的精华，又特别为中国读者考虑，删繁就简，精心筛选和编撰了适合中国读者的素材和内容，尤其适合法学院的学生以及希望了解和思考美国司法模式的读者作为基础读物。', 'https://pic.baike.soso.com/ugc/baikepic2/8805/20201110141025-1823603726_jpeg_300_300_14999.jpg/0', 46.50, 0, 99, '9787301206430', '中国法制出版社', '中国法制出版社', '', 436, '2012-06-24 00:00:00');
INSERT INTO `bms_book` VALUES (5, '洞穴奇案', '2020-12-24 00:00:00', '2020-12-26 00:00:00', 9, '《洞穴奇案》讲述的是五名洞穴探险人受困山洞，无法在短期内获救。为了维生以待救援，大家约定抽签吃掉其中一人，牺牲他以救活其余四人。', 'http://image12.bookschina.com/2019/20190918/1/s8167461.jpg', 53.10, 0, 99, '9787108039873', '【美】彼得 ・ 萨伯（Peter Suber），译者：陈福勇 张世泰    后浪', '九州出版社', '', 157, '2012-04-01 00:00:00');
INSERT INTO `bms_book` VALUES (7, '圆圈正义', '2020-12-24 00:00:00', '2020-12-26 00:00:00', 9, '《圆圈正义》一书共收录作者的49篇随笔文章,分为“圆圈正义”“何谓榜样”“道德谴责的打开方式”“身负权力 各自珍重”“法律人的理智和多数人的情感”“生命的尊严”“心怀永恒 活在当下”七大部分，作者在本书中运用特有的坦诚、自省而尖锐的笔调，探讨了法律、正义、道德的理念与现实、分析了如社会热点案件、分享了自身求学经验和对人生的思考。', 'http://image12.bookschina.com/2019/20190918/1/s8167461.jpg', 31.70, 0, 99, '9787521603774', '罗翔', '中国法制出版社', '', 307, '2019-08-26 00:00:00');
