INSERT INTO `bms_book` VALUES (66, '营造法式', '2020-12-24 00:00:00', '2020-12-26 00:00:00', 11, '营造法式》是北宋时期将作监李诫组织编撰的由官方颁行的一部建筑设计学著作，完成于北宋元符三年（1100年），首次刊行于北宋崇宁二年（1103年）。', 'http://image31.bookschina.com/small/52/35/1587352.jpg', 94.10, 0, 99, '9787010056241', '[宋]李诫', '重庆出版社', '', 422, '2006-09-01 00:00:00');
