INSERT INTO `bms_book` VALUES (83, '半小时漫画中国史5', '2020-12-24 00:00:00', '2020-12-26 00:00:00', 2, '中国史大结局！笑着笑着，大清就亡了！漫画科普开创者混子哥陈磊新作！其实是一本严谨的极简中国史！', 'https://g-search3.alicdn.com/img/bao/uploaded/i4/i1/2759802752/O1CN01yxCb7r1WCSbm8IRqn_!!0-item_pic.jpg_580x580Q90.jpg_.webp', 48.90, 0, 99, '9787544389372', '陈磊 著;读客文化 出品', '文汇出版社', '', 248, '2019-11-01 00:00:00');
