INSERT INTO `bms_book` VALUES (125, '普林斯顿微积分读本（修订版）', '2020-12-24 00:00:00', '2020-12-26 00:00:00', 7, '本书阐述了求解微积分的技巧, 详细讲解了微积分基础、极限、连续、微分、导数的应用、积分、无穷级数、泰勒级数与幂级数等内容，旨在教会读者如何思考问题从而找到解题 所需的知识点, 着重训练大家自己解答问题的能力.', 'http://image12.bookschina.com/2016/20161104/s7311908.jpg', 67.30, 0, 99, '9787115435590', '【美】阿德里安・班纳', '人民邮电出版社', '', 668, '2016-10-01 00:00:00');
