INSERT INTO `bms_book` VALUES (245, '沉思录', '2020-12-24 00:00:00', '2020-12-26 00:00:00', 3, '《沉思录》是古罗马皇帝奥勒留写给自己的书，内容大部分是他在鞍马劳顿中写成的。作品来自奥勒留对身羁宫廷的自己和自己所处混乱世界的感受，追求一种冷静而达观的生活。这部著作是斯多葛学派的一个里程碑。', 'https://g-search3.alicdn.com/img/bao/uploaded/i4/i1/2041592426/O1CN01dKY2t81Tn9SexDG24_!!2041592426-0-lubanu-s.jpg_580x580Q90.jpg_.webp', 16.60, 0, 99, '9787802115743', '马可.奥勒留 青豆书坊 出品', '中央编译出版社', '', 211, '2008-02-01 00:00:00');
INSERT INTO `bms_book` VALUES (246, '理想国', '2020-12-24 00:00:00', '2020-12-26 00:00:00', 3, '《理想国》涉及柏拉图思想体系的各个方面，包括哲学、伦理、教育、文艺、政治等内容，主要是探讨理想国家的问题。', 'https://g-search1.alicdn.com/img/bao/uploaded/i4/imgextra/i3/54222896/O1CN01vUKkkR1XGPeKbkHbe_!!0-saturn_solar.jpg_580x580Q90.jpg_.webp', 31.20, 0, 99, '9787100017565', '柏拉图', '中国华侨出版社', '', 448, '1986-06-01 00:00:00');
